mapper.update:
update product set
state='0'
where id=#{id};

update product set
state='1'
where id=#{id};

update product set
bannerItemId=#{bannerItemId}
where id=#{id};

update product set
brandId=#{brandId}
where id=#{id};

update product set
mainImgUrl=#{mainImgUrl},
imgUrl1=#{imgUrl1},imgUrl2=#{imgUrl2},imgUrl3=#{imgUrl3},imgUrl4=#{imgUrl4}
where id=#{id};

update product set
name=#{name},description=#{description},price=#{price},inventory=#{inventory},
salesCount=#{salesCount},mainImgUrl=#{mainImgUrl},parentCategoryId=#{parentCategoryId},
categoryId=#{categoryId}
where id=#{id};

mapper.select:
select * from
product where bannerItemId=#{bannerItemId}
and state
='1';

select * from
product where brandId=#{brandId}
and state
='1';
